package com.tencent.ttpic.openapi.filter;

import com.tencent.aekit.openrender.UniformParam.FloatParam;
import com.tencent.filter.BaseFilter;

public class ContrastFilter
  extends BaseFilter
{
  public static final String FRAGMENT_SHADER_V = "";
  private float mBrightness = 1.0F;
  private float mContrast = 1.0F;
  private float mSaturation = 1.0F;
  
  public ContrastFilter()
  {
    super("");
    initParams();
  }
  
  private void initParams()
  {
    addParam(new UniformParam.FloatParam("contrast", this.mContrast));
    addParam(new UniformParam.FloatParam("saturation", this.mSaturation));
    addParam(new UniformParam.FloatParam("brightness", this.mBrightness));
  }
  
  public boolean needRender()
  {
    return Math.abs(this.mContrast - 1.0F) > 0.001D;
  }
  
  public void setContrastLevel(int paramInt)
  {
    setContrastValue(paramInt / 100.0F * 0.2F + 1.0F);
  }
  
  public void setContrastValue(float paramFloat)
  {
    this.mContrast = paramFloat;
    initParams();
  }
}


